import logo from './logo.svg';
import './App.css';
import { Card3 } from './Card';

// Object Typing
// In JavaScript, an object can have any property. In TypeScript, you need to create a type alias for the object.
type User = {
  name: string;
  age: number;
  isAdmin?: boolean; // optional property
};

let user: User = {
  name: "Alice",
  age: 25,
};

// Intersection Types (&)
type Admin = User & {
  adminId: string;
};


function App() {
  let x = 0;   // x will be treated as a number.
  // let x: number = 0; I explicitly stated that it will be a number.
  // x = "Hello"; ❌ Error: string is not assignable to number

  // When declaring a variable without a value, you must specify the type.
