    // names[0] = "Charlie";  // ✅
    // names[1] = 42;         ❌ Error: A number cannot be assigned.

    // To create a multi-type array, you use Union.
    let values: (string | number)[] = ["Alice", 42, "Bob"];

    // Union Types (|)

    let age: number | string;
    // age = 25;        ✅
    // age = "Twenty";  ✅
